use std::collections::HashSet;

fn main() {
    //let input = String::from("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg");
    let input = String::from("");

    let unique_length = 14;
    for i in unique_length - 1..input.len() {
        let mut seen = HashSet::<char>::new();
        for j in 0..unique_length {
            if i - j < input.len() {
                seen.insert(input.chars().nth(i - j).unwrap());
            }
        }
        //println!("{:?}", seen);
        if seen.len() == unique_length {
            println!("{}", i + 1);
            break;
        }
    }
}
